import React, { useState } from 'react';
import detailStore from '../store/store';
import ICONS from '../icons/icons';
import '../styles/detail.component.style.scss';

function CommentDetail({ detail }) {
	const [user, setUser] = useState({ like: detailStore.getLikes(detail._id) });
	const [likeClick, setLike] = useState(false);

	function likeUp() {
		setLike(true);
	}

	return detail ? (
		<div className="detail-holder  flex-col">
			<div className="detail-description">
				<p>{detail.text}</p>
			</div>
			<div className="detail-bot flex-row icon">
				<div>
					<img
						src={ICONS.like}
						onClick={
							likeUp
							//addlike();
						}
						alt="popularity"
						title="popularity"
					/>
					<span>{detail.likes.length}</span>
				</div>
			</div>
		</div>
	) : null;
}

export default CommentDetail;
