import React, { useState } from 'react';
import detailStore from '../store/store';
import ICONS from '../icons/icons';
import '../styles/detail.component.style.scss';

function CommentDetail({ detail }) {
	const [user, setUser] = useState([]);
	const [likeClick, setLike] = useState(false);

	function likeUp() {
		setLike(true);
	}
	if (detail) {
		{
			like: detailStore.getLikes(detail._id);
		}
	}
	console.log(detail);
	return detail ? (
		<div className="detail-holder comment-holder  flex-col">
			<div className="detail-description">
				<p>{detail.text}</p>
			</div>
			<div className="detail-bot flex-row icon">
				<div>
					<img
						src={ICONS.like}
						onClick={
							likeUp
							//addlike();
						}
						alt="popularity"
						title="popularity"
					/>
					<span>{detail.likes}</span>
				</div>
			</div>
		</div>
	) : (
		<div className="detail-holder comment-holder  flex-col">
			<form action="/info/comment" method="post">
				<label htmlFor="text">
					<input
						placeholder="Your comment here..."
						className="detail-description comment-imput"
						type="text"
						name="text"
						id="text"
					/>
				</label>
			</form>
		</div>
	);
}

export default CommentDetail;
