import React, { useState, useEffect } from 'react';
import detailStore from '../store/store';
import ICONS from '../icons/icons';
import { addCommentActions } from '../actions/userActions';
import '../styles/detail.component.style.scss';

function CommentDetail({ detail }) {
	const user = detailStore.getUser(detailStore.getUser());
	const [likeClick, setLike] = useState(false);
	const [textComment, setTextComment] = useState('');

	useEffect(() => {
		detailStore.addChangeListener(onChange);
		return () => {
			return detailStore.removeChangeListener(onChange);
		};
	}, []);

	function onChange() {
		setTextComment('');
	}

	function likeUp() {
		setLike(true);
	}

	function submitText() {
		addCommentActions(user._id, textComment);
	}
	return detail ? (
		<div className="detail-holder comment-holder  flex-col">
			<div className="detail-description">
				<p>{detail.text}</p>
			</div>
			<div className="detail-bot flex-row icon">
				<div>
					<img
						src={ICONS.like}
						onClick={
							likeUp
							//addlike();
						}
						alt="popularity"
						title="popularity"
					/>
					<span>{detail.likes}</span>
				</div>
			</div>
		</div>
	) : (
		<div className="detail-holder comment-holder  flex-col">
			<form onSubmit={submitText} action="javascript:void(0);">
				<div>
					<label htmlFor="text">
						<input
							placeholder="Your comment here..."
							className="detail-description comment-input"
							onChange={(event) => setTextComment(event.target.value)}
							value={textComment}
							type="text"
							name="text"
							id="text"
						/>
					</label>
					<label htmlFor="user">
						<input type="hidden" name="user" value={user} />
					</label>
				</div>
			</form>
		</div>
	);
}

export default CommentDetail;
