import { EventEmitter } from 'events';
import dispatcher from '../dispatcher/app.dispatcher';
import actionTypes from '../actions/actionTypes';

const CHANGE_EVENT = 'change';
let detail = [];
let message;
class DBStore extends EventEmitter {


    addChangeListener(callback) {
        this.on(CHANGE_EVENT, callback);
    }

    removeChangeListener(callback) {
        this.removeListener(CHANGE_EVENT, callback);
    }

    emitChange() {
        this.emit(CHANGE_EVENT);
    }

    // eslint-disable-next-line class-methods-use-this
    getDetail() {
        return detail;
    }

    // eslint-disable-next-line class-methods-use-this
    getUser() {
        return JSON.parse(global.sessionStorage.getItem('user'));
    }

    // eslint-disable-next-line class-methods-use-this
    logout() {
        global.sessionStorage.removeItem('user');
        global.sessionStorage.removeItem('access-token');
        this.emitChange();
    }

    // eslint-disable-next-line class-methods-use-this
    getToken() {
        return global.sessionStorage.getItem('access-token')
    }

    // eslint-disable-next-line class-methods-use-this
    getMessage() {
        return message;
    }

}

const store = new DBStore();
dispatcher.register((action) => {
    console.log(action)
    switch (action.type) {
        case actionTypes.LOAD_DETAIL:
            detail = action.data;
            store.emitChange();
            break;
        case actionTypes.LOGIN:
            global.sessionStorage.setItem('user', JSON.stringify(action.data.user))
            store.emitChange();
            break;
        case actionTypes.ERROR:
            console.log(action)
            switch (action.data.code) {
                case 1:
                    store.logout();
                    message = action.data.message
                    break;
                default:
                    break;
            }
            store.emitChange();
            break;
        default:
            break;
    }
});

export default store;
