import React, { useState, useEffect } from 'react';
import store from '../store/store';
import { Redirect, Link } from 'react-router-dom';
import {
	RadioGroup,
	Radio,
	FormLabel,
	FormControlLabel,
	TextareaAutosize
} from '@material-ui/core';
import '../styles/addEntry.component.styles.scss';

function AddEntry() {
	const [speciality, setSpeciality] = useState('frontend');
	const [category, setCategory] = useState('junior');
	const [content, setContent] = useState('');
	const [difficulty, setDificulty] = useState(1);

	const [register, setRegister] = useState(null);

	useEffect(() => {
		store.addChangeListener(onChange);

		return () => {
			return store.removeChangeListener(onChange);
		};
	}, [register]);

	function onChange() {
		return 0;
	}

	function handleCategory(event) {
		setCategory(event.target.value);
	}
	function handleSpeciality(event) {
		setSpeciality(event.target.value);
	}

	function handleSubmit(event) {
		event.preventDefault();
		console.log(speciality, category, content);
	}

	return (
		<>
			<div className="flex-col newEntry-container">
				<form onSubmit={handleSubmit} className="flex-col addEntry-holder">
					<h1>ADD NEW ENTRY </h1>
					<FormLabel className="addEntry-label" component="legend">
						Select one Category
					</FormLabel>
					<RadioGroup
						aria-label="Category"
						name="category"
						value={category}
						onChange={handleCategory}
					>
						<FormControlLabel
							value="senior"
							control={<Radio />}
							label="SENIOR"
						/>
						<FormControlLabel
							value="junior"
							control={<Radio />}
							label="JUNIOR"
						/>
					</RadioGroup>
					<FormLabel className="addEntry-label" component="legend">
						Select one Speciality
					</FormLabel>
					<RadioGroup
						aria-label="Speciality"
						name="speciality"
						value={speciality}
						onChange={handleSpeciality}
					>
						<FormControlLabel
							value="frontend"
							control={<Radio />}
							label="Front-End"
						/>
						<FormControlLabel
							value="backend"
							control={<Radio />}
							label="Back-End"
						/>
						<FormControlLabel
							value="fullstack"
							control={<Radio />}
							label="Full-Stack"
						/>
					</RadioGroup>
					<FormLabel className="addEntry-label" component="legend">
						Description
					</FormLabel>
					<TextareaAutosize
						rowsMin={4}
						aria-label="maximum height"
						name="content"
						placeholder="Enter the description here..."
						value={content}
						onChange={(event) => setContent(event.target.value)}
					/>

					<TextField
						id="outlined-name"
						label="Name"
						className={classes.textField}
						value={this.state.name}
						onChange={this.handleChange('name')}
						margin="normal"
						variant="outlined"
					/>

					<button type="submit">here</button>
				</form>
			</div>
		</>
	);
}

export default AddEntry;
