import React, { useState, useEffect } from 'react';

import { loadEntries } from '../actions/userActions';
import Detail from './detail.component';
import store from '../store/store';
import '../styles/entry.load.style.scss';

function UserEntries({ type }) {
	const [detail, setDetail] = useState(null);
	useEffect(() => {
		detailStore.addChangeListener(onChange);
		if (detail.length === 0) loadEntries(type, store.getUser._id);
		return () => {
			return detailStore.removeChangeListener(onChange);
		};
	}, [detail.length]);

	function onChange() {
		setDetail(detailStore.getDetail());
	}

	return (
		<>
			{detail.length !== 0 &&
				detail.map((current) => {
					return <Detail key={current._id} detail={current}></Detail>;
				})}

			{detail.length === 0 && (
				<div className="loading">
					<img
						src="https://thumbs.gfycat.com/FrailFirsthandAnnashummingbird-max-1mb.gif"
						alt="loading"
					/>
				</div>
			)}
		</>
	);
}

export default UserEntries;
