import React, { useState, useEffect } from 'react';
import Store from '../../store/store';
import { loadDetails } from '../../actions/userActions';
import ICONS from '../../icons/icons';

function Detail() {
	const [detail, setDetail] = useState([]);
	const [user, setUser] = useState({});
	setUser({ fav: true });
	useEffect(() => {
		Store.addChangeListener(onChange);
		if (detail.length === undefined) {
			loadDetails();
		}
		return () => Store.removeChangeListener(onChange);
	}, []);

	function onChange() {
		setDetail(Store.getDetail());
	}
	return (
		<div className="detail-holder">
			<div className="detail-info-holder">
				<div className="detail-info">
					<img
						className="detail-info__favorites"
						src={user.fav ? ICONS.favorite : ICONS.noFavorite}
						alt="star"
					/>
					<div className="detail-info__icons">
						{detail.junior && <img src={ICONS.junior} alt="junior" />}
						{detail.senior && <img src={ICONS.senior} alt="senior" />}
						{detail.backEnd && <img src={ICONS.backend} alt="backend" />}
						{detail.frontEnd && <img src={ICONS.frontend} alt="frontend" />}
						{detail.have_file && <img src={ICONS.file} alt="files" />}
						{detail.is_question ? (
							<img src={ICONS.question} alt="question" />
						) : (
							<img src={ICONS.tech} alt="tech" />
						)}
					</div>
				</div>
			</div>
			<div className="detail-description"></div>
			<div className="detail-bot">
				<img src={ICONS.comments} alt="comments" />
				{detail.entryCommentList && (
					<span>{detail.entryCommentList.length}</span>
				)}
				<img src={ICONS.popularity} alt="popularity" />
				<span>{detail.entryPopularity}</span>
				<img src={ICONS.rating} alt="rating" />
				<span>{detail.entryRating}</span>
			</div>
		</div>
	);
}

export default Detail;
