import React, { useState, useEffect } from 'react';
import '../styles/login.component.style.scss';
import { registerActions } from '../actions/userActions';
import store from '../store/store';
import { Redirect, Link } from 'react-router-dom';

function AddEntry() {
	const [name, setName] = useState('');
	const [bday, setBday] = useState('');
	const [bootCamp, setBootCamp] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [register, setRegister] = useState(null);

	useEffect(() => {
		store.addChangeListener(onChange);

		return () => {
			return store.removeChangeListener(onChange);
		};
	}, [register]);

	function onChange() {
		setRegister(store.getRegister());
	}

	if (register) {
		store.setRegister();
		return <Redirect to="/" />;
	}
	function addNewEntry() {
		return true;
	}

	return (
		<div className="login-super">
			<div className="login-holder flex-col">
				<h1>NEW ENTRY</h1>
				<div className="login-imputs flex-col">
					<div className="flex-col">
						<form
							className="flex-col"
							action="javascript:void(0);"
							onSubmit={addNewEntry}
						>
							<label className="flex-col" htmlFor="speciality">
								{' '}
								speciality
								<input
									type="radio"
									id="backend"
									value={backend}
									name="speciality"
								/>
								<input
									type="radio"
									id="frontend"
									value={frontend}
									name="speciality"
								/>
								<input
									type="radio"
									id="fullstack"
									value={fullstack}
									name="fullstack"
								/>
							</label>
							<label className="flex-col" htmlFor="categoty">
								{' '}
								Birth Day
								<input
									type="radio"
									id="junior"
									value={junior}
									name="category"
								/>
								<input
									type="radio"
									id="senior"
									value={senior}
									name="category"
								/>
							</label>
							<label className="flex-col" htmlFor="bootCamp">
								{' '}
								bootCamp
								<input
									type="number"
									id="bootCamp"
									placeholder="ej. 202007"
									value={bootCamp}
									onChange={(event) => setBootCamp(event.target.value.trim())}
									name="bootCamp"
									required
								/>
							</label>
							<label className="flex-col" htmlFor="email">
								{' '}
								email
								<input
									type="email"
									id="email"
									value={email}
									onChange={(event) => setEmail(event.target.value.trim())}
									name="email"
									required
								/>
							</label>
							<label htmlFor="password" className="flex-col">
								{' '}
								password
								<input
									type="password"
									pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}"
									title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"
									id="password"
									value={password}
									name="password"
									onChange={(event) => setPassword(event.target.value.trim())}
									required
								/>
							</label>
							<button className="login-button_log" type="submit">
								{' '}
								Submit
							</button>
							<Link to="/">Back to Login</Link>
						</form>
					</div>
				</div>
			</div>
		</div>
	);
}

export default AddEntry;
