import React, { useState } from 'react';
import '../styles/login.component.style.scss';
import navIcons from '../icons/navIcons';
import { loginActions } from '../actions/userActions';
import store from '../store/store';
import { Redirect } from 'react-router-dom';

function Login() {
	const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');
	const [user, setUser] = useState(null);

	useEffect(() => {
		detailStore.addChangeListener(onChange);
		loadDetails();
		return () => {
			return detailStore.removeChangeListener(onChange);
		};
	}, [detail.length]);

	function onChange() {
		setUser(store.getUser());
	}
	function sendUser(a, b) {
		loginActions(a, b);
	}
	if (user) {
		return <Redirect to="/" />;
	}

	return (
		<div className="login-super">
			<div className="login-holder flex-col">
				<div className="login-holder_img flex-item">
					<img src={navIcons.login} alt="Welcome!" />
				</div>
				<div className="login-imputs flex-col">
					<div className="flex-col">
						<label className="flex-col" htmlFor="username">
							{' '}
							username
							<input
								type="text"
								id="username"
								value={username}
								onChange={(event) => setUsername(event.target.value.trim())}
								name="username"
							/>
						</label>
						<label htmlFor="password" className="flex-col">
							{' '}
							password
							<input
								type="password"
								id="password"
								value={password}
								name="password"
								onChange={(event) => setPassword(event.target.value.trim())}
							/>
						</label>
						<button
							onClick={() => sendUser(username, password)}
							className="login-button_log"
							type="submit"
						>
							{' '}
							Log In
						</button>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Login;
