import React, { useState, useEffect } from 'react';
import detailStore from '../../store/store';
import ICONS from '../../icons/icons';
import '../../styles/detail.component.style.scss';

function Detail(detail) {
	const [user, setUser] = useState({ fav: false });
	const [popClick, setPop] = useState(false);

	useEffect(() => {
		detailStore.addChangeListener(onChange);

		return () => {
			return detailStore.removeChangeListener(onChange);
		};
	}, []);

	function upPop() {
		setPop(true);
	}
	function changeFav() {
		if (user.fav) {
			setUser({ fav: false });
			// addFav()
		} else {
			setUser({ fav: true });
			// delFav()
		}
	}
	function onChange() {
		setUser(detailStore.getDetail());
	}
	return (
		<div key={detail._id} className="detail-holder  flex-col">
			<div className="detail-info-holder ">
				<div className="detail-info flex-row icon">
					<img
						className="detail-info__favorites "
						src={user.fav ? ICONS.favorite : ICONS.noFavorite}
						onClick={changeFav}
						alt="star"
					/>
					<div className="detail-info__icons icon flex-row">
						{detail.isJunior && (
							<img src={ICONS.junior} alt="junior" title="junior" />
						)}
						{detail.isSenior && (
							<img src={ICONS.senior} alt="senior" title="senior" />
						)}
						{detail.isBackend && (
							<img src={ICONS.backend} alt="backend" title="backend" />
						)}
						{detail.isFrontend && <img src={ICONS.frontend} alt="frontend" />}
						{detail.isFullstack && (
							<img src={ICONS.fullstack} alt="fullstack" />
						)}
						{detail.haveFile && <img src={ICONS.file} alt="files" />}
						{detail.isQuestion ? (
							<img src={ICONS.question} alt="question" />
						) : (
							<img src={ICONS.tech} alt="tech" />
						)}
					</div>
				</div>
			</div>
			<div className="detail-description">
				<p>{detail.description}</p>
			</div>
			<div className="detail-bot flex-row icon">
				{detail.entryCommentList && (
					<div>
						<img src={ICONS.comments} alt="comments" />
						<span>{detail.entryCommentList.length}</span>
					</div>
				)}

				<div>
					<img
						src={popClick ? ICONS.addPopularity : ICONS.popularity}
						onClick={
							upPop
							//addPopularity();
						}
						alt="popularity"
					/>
					<span>{detail.entryPopularity}</span>
				</div>

				<div>
					<img src={ICONS.rating} alt="rating" />
					<span>{detail.entryRating}</span>
				</div>
			</div>
		</div>
	);
}

export default Detail;
