import React, { useState, useEffect } from 'react';
import './App.css';
import Entry from './components/entry.component';
import Home from './components/home.component'
import { Route, Switch } from "react-router-dom";
import Nav from './components/nav.component';
import Logo from './components/logo.component'
import Login from './components/login.component';
import Registrer from './components/register.component';
import Profile from './components/profile.component'
import { Fragment } from 'react';
import store from './store/store'
import { loadDetails, loadEntries } from './actions/userActions';

function App() {
  const [message, setMessage] = useState(false);
  const [user, setUser] = useState(null);
  const [details, setDetails] = useState(null);
  const [userFavoruites, setUserFavourites] = useState(null);
  const [userEntries, setUserEntries] = useState(null);

  console.log(userFavoruites)
  useEffect(() => {
    store.addChangeListener(onChange);
    let usu = store.getUser();
    if (!details) loadDetails()
    if (!user) {
      setUser(usu);
    }
    if (user) {
      setUserFavourites(loadEntries('favourites', user._id));
      setUserEntries(loadEntries('entries', user._id))
    }
    return () => {
      return store.removeChangeListener(onChange);
    };
  }, [user, details]);

  function onChange() {
    setUser(store.getUser())
    setMessage(store.getMessage())
    setDetails(store.getDetail())

  }

  return (
    <div>

      {!user && (
        <Switch>
          <Route path='/' exact render={props =>
            <Fragment>
              <Logo />
              <Login />
              {!!message && message}
            </Fragment>}
          />
          <Route path='/register' render={props =>
            <Fragment>
              <Logo />
              <Registrer />
              {!!message && message}
            </Fragment>}
          />
        </Switch>)}

      {!!user && (

        <Switch>
          <Route path='/' exact render={props =>
            <Fragment>
              <Logo />
              <Home />
              {!!message && message}
            </Fragment>
          } />


          <Route path='/list' render={props =>
            <Fragment>
              <Nav />
              {!!details && <Entry list={details} />}
              {!!message && message}
            </Fragment>
          } />
          <Route path='/profile' render={props =>
            <Fragment>
              <Nav />
              <Profile />
              {!!message && message}
            </Fragment>
          } />
          <Route path='/user/favoruites' render={props =>
            <Fragment>
              <Nav />
              <Entry list={userFavoruites} />
              {!!message && message}
            </Fragment>
          } />
          <Route path='/user/entries' render={props =>
            <Fragment>
              <Nav />
              <Entry list={userEntries} />
              {!!message && message}
            </Fragment>
          } />

        </Switch>)
      }
    </div >
  );
}

export default App;
