import React, { useState, useEffect } from 'react';
import '../styles/login.component.style.scss';
import { registerActions } from '../actions/userActions';
import store from '../store/store';
import { Redirect } from 'react-router-dom';

function Register() {
	const [name, setName] = useState('');
	const [bday, setBday] = useState('');
	const [bootCamp, setBootCamp] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [user, setUser] = useState(null);

	useEffect(() => {
		store.addChangeListener(onChange);
		if (!user) {
			setUser(store.getUser());
		}
		return () => {
			return store.removeChangeListener(onChange);
		};
	}, [user]);

	function onChange() {
		setUser(store.getUser());
	}
	function sendNewUser(name, bootCamp, email, password, bday) {
		registerActions(name, bootCamp, email, password, bday);
	}
	if (user) {
		return <Redirect to="/" />;
	}

	return (
		<div className="login-super">
			<div className="login-holder flex-col">
				<h1>REGISTER</h1>
				<div className="login-imputs flex-col">
					<div className="flex-col">
						<form
							action="javascript:sendNewUser(name, bootCamp, email, password, bday);"
							method="post"
						>
							<label className="flex-col" htmlFor="name">
								{' '}
								name
								<input
									type="text"
									id="name"
									value={name}
									onChange={(event) => setName(event.target.value.trim())}
									name="name"
									required
								/>
							</label>
							<label className="flex-col" htmlFor="dbay">
								{' '}
								Birth Day
								<input
									type="date"
									id="bday"
									value={bday}
									onChange={(event) => setBday(event.target.value.trim())}
									name="bday"
								/>
							</label>
							<label className="flex-col" htmlFor="bootCamp">
								{' '}
								bootCamp
								<input
									type="number"
									id="bootCamp"
									placeholder="ej. 202007"
									value={bootCamp}
									onChange={(event) => setBootCamp(event.target.value.trim())}
									name="bootCamp"
									required
								/>
							</label>
							<label className="flex-col" htmlFor="email">
								{' '}
								email
								<input
									type="text"
									id="email"
									value={email}
									onChange={(event) => setEmail(event.target.value.trim())}
									name="email"
									required
								/>
							</label>
							<label htmlFor="password" className="flex-col">
								{' '}
								password
								<input
									type="password"
									pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}"
									title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"
									id="password"
									value={password}
									name="password"
									onChange={(event) => setPassword(event.target.value.trim())}
									required
								/>
							</label>
							<input
								className="login-button_log"
								type="submit"
								value="Submit"
							></input>
						</form>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Register;
