import React, { useState, useEffect } from 'react';
import '../styles/login.component.style.scss';
import { loadListActions } from '../actions/userActions';
import store from '../store/store';
import { Redirect, Link } from 'react-router-dom';

function Profile() {
	const fav = 'favourites';
	const entries = 'entries';
	const [user, setUser] = useState(store.getUser());
	useEffect(() => {
		store.addChangeListener(onChange);

		return () => {
			return store.removeChangeListener(onChange);
		};
	}, [user]);

	function onChange() {
		setUser(store.getUser());
	}
	function loadEntries(listSelect) {
		loadEntries(entries);
	}

	if (!user) {
		return <Redirect to="/" />;
	}

	return (
		<>
			<div className="profile-holder">
				<h1>Profile</h1>
				<div className="profile-info">
					<p>
						Name: <span>{user.name}</span>
					</p>
					<p>
						Email: <span>{user.email}</span>
					</p>
					<p>
						Bday: <span>{user.bday}</span>
					</p>
					<p>
						Nº Entries:{' '}
						<span
							onClick={() => {
								loadEntries(entries);
							}}
						>
							{user.user_entries_list.length}
						</span>
					</p>
					<p>
						Nº Comments: <span>{user.user_comment_list.length}</span>
					</p>
					<p>
						Favorites:{' '}
						<span
							onClick={() => {
								loadEntries(fav);
							}}
						>
							{user.user_fav_list.length}
						</span>
					</p>
				</div>
			</div>
		</>
	);
}

export default Profile;
