import React, { useState, useEffect } from 'react';
import '../styles/login.component.style.scss';
import { registerActions } from '../actions/userActions';
import store from '../store/store';
import { Redirect, Link } from 'react-router-dom';
import md5 from 'md5';

function Profile() {
	const [user, setUser] = useState(store.setUser());
	useEffect(() => {
		store.addChangeListener(onChange);

		return () => {
			return store.removeChangeListener(onChange);
		};
	}, [register]);

	function onChange() {
		setRegister(store.getRegister());
	}

	if (!user) {
		return <Redirect to="/" />;
	}

	return (
		<>
			<div className="profile-holder">
				<h1>Profile</h1>
				<div className="profile-info">
					<p>
						Name: <span>user.name</span>
					</p>
					<p>
						Email: <span>user.email</span>
					</p>
					<p>
						Bday: <span>user.bday</span>
					</p>
					<p>
						Nº Entries: <span>user.user_entries_list.lenth</span>
					</p>
					<p>
						Nº Comments: <span>user.user_comments_list.length</span>
					</p>
					<p>
						Favorites: <span>user.user_fav_list.length</span>
					</p>
				</div>
			</div>
		</>
	);
}

export default Profile;
