import React, { useState, useEffect } from 'react';

import { loadEntries, loadDetails } from '../actions/userActions';
import Detail from './detail.component';
import CommentDetail from './commentDetail.compoent';
import store from '../store/store';
import '../styles/entry.load.style.scss';

function EntryComment({ props }) {
	const [detail, setDetail] = useState([]);
	const [comments, setComments] = useState([]);
	console.log(props.match.params.entryId);
	// const usu = store.getUser();
	useEffect(() => {
		store.addChangeListener(onChange);
		if (detail.length === 0) {
			loadDetails();
		}
		return () => {
			return store.removeChangeListener(onChange);
		};
	}, [detail.length]);

	function onChange() {
		setDetail(store.getOneEntry());
		setComments(store.getComments());
	}

	return (
		<>
			{detail.length !== 0 && <Detail detail={detail}></Detail>}
			{detail.length !== 0 &&
				comments.map((current) => {
					return (
						<CommentDetail key={current._id} comment={current}></CommentDetail>
					);
				})}

			{detail.length === 0 && (
				<div className="loading">
					<h1>Maybe there is nothing here...</h1>
					<img
						src="https://thumbs.gfycat.com/FrailFirsthandAnnashummingbird-max-1mb.gif"
						alt="loading"
					/>
				</div>
			)}
		</>
	);
}

export default EntryComment;
