import axios from 'axios';
import dispatcher from '../dispatcher/app.dispatcher';
import actionTypes from './actionTypes';
import URL from './URL';

export function loadDetails() {
    const token = global.sessionStorage.getItem('access-token')
    axios.get(URL.GET_ENTRIES, { headers: { 'access-token': token } }).then((detailsRecives) => {


        if (detailsRecives.status === 200) {
            dispatcher.dispatch({
                type: actionTypes.LOAD_DETAIL,
                data: detailsRecives.data
            })
        } else {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: detailsRecives.data
            })
        }


    }).catch((detailsRecives) => {
        console.log(detailsRecives)
        dispatcher.dispatch({
            type: actionTypes.ERROR,
            data: detailsRecives.response.data
        })
    })

}


export function loginActions(username, password) {
    return axios.post(URL.POST_LOGIN, { username, password }).then((detailsRecives) => {

        if (detailsRecives.status === 200) {
            global.sessionStorage.setItem('access-token', detailsRecives.data.token)
            dispatcher.dispatch({
                type: actionTypes.LOGIN,
                data: detailsRecives.data
            })
        }
        else {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: detailsRecives.data
            })
        }


    }).catch((detailsRecives) => {
        dispatcher.dispatch({
            type: actionTypes.ERROR,
            data: detailsRecives.response.data
        })
    }
    )
}

export function registerActions(name, bootCamp, email, password, bday) {
    return axios.post(URL.POST_REGISTER, { name, bootCamp, email, password, bday }).then((detailsRecives) => {

        if (detailsRecives.status === 200) {
            dispatcher.dispatch({
                type: actionTypes.REGISTER,
                data: detailsRecives.data
            })
        }
        else {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: detailsRecives.data
            })
        }


    }).catch((detailsRecives) => {
        dispatcher.dispatch({
            type: actionTypes.ERROR,
            data: detailsRecives.response.data
        })
    }
    )
}

export function loadEntries(entriesTipe, userId) {

    const token = global.sessionStorage.getItem('access-token')
    return axios.get(`http://localhost:4200/api/user/${entriesTipe}?userId=${userId}`, { headers: { 'access-token': token } }).then((detailsRecives) => {

        if (detailsRecives.status === 200) {
            dispatcher.dispatch({
                type: actionTypes.LOAD_ENTRIES,
                data: detailsRecives.data
            })
        }
        else {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: detailsRecives.data
            })
        }


    }).catch((detailsRecives) => {
        dispatcher.dispatch({
            type: actionTypes.ERROR,
            data: detailsRecives.response.data
        })
    }
    )
}

export function loadComments() {

    const token = global.sessionStorage.getItem('access-token')
    return axios.get(`http://localhost:4200/api/comments`, { headers: { 'access-token': token } }).then((detailsRecives) => {

        if (detailsRecives.status === 200) {
            dispatcher.dispatch({
                type: actionTypes.LOAD_COMMENTS,
                data: detailsRecives.data
            })
        }
        else {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: detailsRecives.data
            })
        }


    }).catch((detailsRecives) => {
        if (detailsRecives && detailsRecives.response && detailsRecives.response.data) {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: detailsRecives.response.data
            })
        } else {
            dispatcher.dispatch({
                type: actionTypes.ERROR,
                data: { error: { code: 1, message: detailsRecives.toString() } }
            })
        }
    }
    )
}
